INSERT INTO `lienhe` (`id`, `hoten`, `tieude`, `noidung`, `email`, `thoigian`) VALUES
(9, 'Nguyễn Thị Xuân', 'giúp việc cho người nước ngoài', 'Xin chào anh,chị,\nTôi là Xuân 38 tuổi,hiện tại sống ở Hải dương,tiếng anh giao tiếp tốt.Tôi muốn đăng kí giúp việc cho người nước ngoài tại hà nội.Xin cám ơn', 'thixuan201177@hotmail.com', '2014-11-21'),
(10, 'NGUYỄN THỊ THƯƠNG', 'Nhận giúp việc theo giờ', 'Tôi năm nay 23 tuổi nhận giúp việc theo giờ cho người nước ngoài các buổi sáng trong tuần, có khả năng giao tiep tiếng anh tốt', 'thuong22.vcu@gmail.com', '2014-12-31'),
(11, 'Nguyen Thi Huong', 'Giup viec theo gio', 'Toi can mot Nguoi giup viec theo. gio Ngay 2 tieng tuan 2 buoi gia la bao nhieu 1 tuan nha toi o quan Tan Binh phuong 12', 'hang.nguyen520@gmail.com', '2015-4-7'),
(12, 'nguyen thu tra', 'giup viec theo gio cho nguoi nuoc ngoai', 'em tên trà sinh năm 90 đã có 3 năm kinh nghiệm giúp việc theo giờ cho người nước ngoài và tạp vụ văn phòng cho người nhật bản.tinh tinh coi mo nhiet tinh tu tin giao tiep tieng anh tieng nhat.do da co kinh nghiem nen lam viec co ky luat trung thuc va co trach nhiem.hien tai em dang can cong viec giup viec gia dinh cho nguoi nuoc ngoai dac biet la nguoi nhat ban', 'tieulan1051990@gmail.com', '2015-4-18'),
(13, 'Nguyễn Xuân Nhật Tài', 'XIN VIEC LAM', 'EM XIN LAM VE SINH CHO CONG TY A.', 'anh_em98600@yahoo.com', '2015-5-11'),
(14, 'Vũ Hoài Linh', 'Giúp việc theo giờ', 'Tôi muốn nhận làm giúp việc theo giờ hãy chỉ cho tôi làm như nào?', 'muaxuan.3000@gmail.com', '2015-5-17'),
(15, 'lê thị thùy', 'xin bảng báo giá', 'công ty em đang cần dịch vụ này nên anh/ chị vui lòng gửi bảng báo giá dịch vụ dọn dẹp văn phòng cho em được không ạ? xin cảm ơn', 'thuyledaiphat@gmail.com', '2015-8-4'),
(16, 'Nguyễn Thị Hồng Nhật', 'Tìm người giúp việc theo giờ', 'Mình muốn tìm người giúp việc dọn dẹp nhà cửa có thể làm việc từ 6 - 9h tối vào 1 ngày trong tuần. Trước hết là ngày 16 hoặc 17/9/2015. Nếu dịch vụ tốt thì mình có thể chuyển thành dịch vụ theo giờ tuần vào ngày cố định để giúp đỡ gia đình.', 'megan.nguyen.vn@gmail.com', '2015-9-15'),
(17, 'Phạm Trần Tường Vy', 'giúp việc nhà cho người nước ngoài', 'Hiện tại Em muốn kiếm công việc giúp việc nhà cho người nước ngoài theo giờ từ 6-9h tối các buổi trong tuần ở TP.HCM. Mong công ty có thể liên hệ cho Em nếu có cv thích hợp . Chân thành cảm ơn.\nSDT: 0933157082', 'phamvypt89@gmail.com', '2015-10-29'),
(18, 'bùi thị quỳnh trang', 'tìm việc làm thêm', 'e là sinh viên em muốn làm thêm giúp việc theo giờ tại công ty.em tự tin là mình có thể hoàn thành tốt công việc được giao.', 'quynhtrang100297@gmail.com', '2015-12-12'),
(19, 'Trần Trung Hiển Mr.', 'Giúp việc nhà theo giờ ', 'Kính gởi quý công ty, tôi quan tâm tới công việc giúp việc nhà theo giờ cho người nước ngoài lý do: tôi biết tiếng Anh (cử nhân Anh văn), cắm hoa, nấu ăn phong cách châu Âu, massage. Tel 0909 906 976', 'paul.lu2016@gmail.com', '2016-1-19'),
(20, 'Thanh Nguyễn', 'Dịch vụ dọn vệ sinh văn phòng ', 'Cần tìm dịch vụ vệ sinh văn phòng 1 lần/1 tuần, (3h/ lần).\nGía 600k/ tháng \nThời gian bắt đầu: qua tết', 'thanhvo@kootoro.com', '2016-2-1');

